package org.rubik.product.domain;import com.fasterxml.jackson.annotation.JsonFormat;import java.math.BigDecimal;import java.time.LocalDate;import java.time.LocalDateTime;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;import org.hibernate.validator.constraints.NotBlank;/** * @author xiajinxin * @since 2016-07-25 */@Entity@Table(name = "PRODUCT")public class Product {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    @NotBlank    @Column(name = "name", length = 30)    private String name;    @Column(name = "image")    private String image;    @Column(name = "manufacturer", length = 100)    private String manufacturer;    @Column(name = "price")    private BigDecimal price;    @JsonFormat(pattern="yyyy-MM-dd")    @Column(name = "release_date")    private LocalDate releaseDate;    @JsonFormat(pattern="yyyy-MM-dd HH:mm:ss")    @Column(name = "active_start_date")    private LocalDateTime activeStartDate;    @JsonFormat(pattern="yyyy-MM-dd HH:mm:ss")    @Column(name = "active_end_date")    private LocalDateTime activeEndDate;    @Column(name = "description")    private String description;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getImage() {        return image;    }    public void setImage(String image) {        this.image = image;    }    public String getManufacturer() {        return manufacturer;    }    public void setManufacturer(String manufacturer) {        this.manufacturer = manufacturer;    }    public BigDecimal getPrice() {        return price;    }    public void setPrice(BigDecimal price) {        this.price = price;    }    public LocalDate getReleaseDate() {        return releaseDate;    }    public void setReleaseDate(LocalDate releaseDate) {        this.releaseDate = releaseDate;    }    public LocalDateTime getActiveStartDate() {        return activeStartDate;    }    public void setActiveStartDate(LocalDateTime activeStartDate) {        this.activeStartDate = activeStartDate;    }    public LocalDateTime getActiveEndDate() {        return activeEndDate;    }    public void setActiveEndDate(LocalDateTime activeEndDate) {        this.activeEndDate = activeEndDate;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }}