package org.rubik.api.domain;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.Table;import org.hibernate.validator.constraints.NotBlank;/** * @author xiajinxin * @since 2016-07-25 */@Entity@Table(name = "product")public class Product {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    @NotBlank    private String name;    private String description;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Product product = (Product) o;        if (getId() != null ? !getId().equals(product.getId()) : product.getId() != null)            return false;        if (getName() != null ? !getName().equals(product.getName()) : product.getName() != null)            return false;        return getDescription() != null ? getDescription().equals(product.getDescription()) : product.getDescription() == null;    }    @Override    public int hashCode() {        int result = getId() != null ? getId().hashCode() : 0;        result = 31 * result + (getName() != null ? getName().hashCode() : 0);        result = 31 * result + (getDescription() != null ? getDescription().hashCode() : 0);        return result;    }}